import Database from 'better-sqlite3';

const db = new Database\('./data/database.db'\);

// Check if admin_path setting exists
const existing = db.prepare\('SELECT * FROM system_settings WHERE key = ?'\).get\('admin_path'\);

if \(!existing\) {
  // Insert default admin path
  db.prepare\('INSERT INTO system_settings \(key, value, updated_at\) VALUES \(?, ?, ?\)'\).run\(
    'admin_path',
    '/admin.html',
    new Date\(\).toISOString\(\)
  \);
  console.log\('✓ 已添加 admin_path 设置，默认值: /admin.html'\);
} else {
  console.log\('✓ admin_path 设置已存在:', existing.value\);
}

db.close\(\);
EOF)

